#----------冒泡排序---------------
$arr = array(12,5,7,9,14,68,9);

$n = count($arr);

for($i=0;$i<$n;$i+=1){
	for($j=$i;$j<$n;$j+=1){
		if($arr[$i]>$arr[$j]){
			$s = $arr[$i];
			$arr[$i]=$arr[$j];
			$arr[$j]=$s;
		}
	}
}
print_r($arr);


